
public class ForloopDemo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for (int i=0;i<2;i++)
                     {
                         

			for (int j=0;j<2;j++)
			{
				
				 System.out.print("A");	


                       for (int k=0;k<2;k++);
				{
					
				
	                          System.out.print("B");
                             }

                      for (int p=0;p<2;p++);
				{
					
				
	                          System.out.print("C"); 
                      }
                      System.out.println();


}
     
}
}
}
